import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "./UnitSet.css";
import { CircularProgressbar } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";

const UnitSet = () => {
  const [openUnit, setOpenUnit] = useState(null);
  const [quizStatus, setQuizStatus] = useState({
    alpha: false,
    finger: false,
    greetings: false,
    grammar: false,
    nounAdj: false,
    verbColors: false,
  });

  const userName = localStorage.getItem("userName");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchQuizStatus = async (lessonId, key) => {
      try {
        const response = await axios.get("http://localhost:5001/api/lessons/get-lesson", {
          params: { lessonId, userName },
        });
        if (response.data.lesson?.quiz_complete) {
          setQuizStatus((prev) => ({ ...prev, [key]: true }));
        }
      } catch (error) {
        console.error(`Error fetching data for lesson ${lessonId}:`, error);
      }
    };

    if (userName) {
      fetchQuizStatus("101", "alpha");
      fetchQuizStatus("102", "finger");
      fetchQuizStatus("103", "greetings");
      fetchQuizStatus("201", "grammar");
      fetchQuizStatus("202", "nounAdj");
      fetchQuizStatus("203", "verbColors");
    }
  }, [userName]);

  const toggleUnit = (unit) => {
    setOpenUnit(openUnit === unit ? null : unit);
  };

  const handleNavigate = (path) => {
    navigate(path);
  };

  const progress = {
    alpha: quizStatus.alpha ? 100 : 0,
    finger: quizStatus.finger ? 100 : 0,
    greetings: quizStatus.greetings ? 100 : 0,
    grammar: quizStatus.grammar ? 100 : 0,
    nounAdj: quizStatus.nounAdj ? 100 : 0,
    verbColors: quizStatus.verbColors ? 100 : 0,
  };

  // Calculate Unit Progress
  const calculateUnitProgress = (lessons) => {
    const totalProgress = lessons.reduce((acc, lesson) => acc + progress[lesson], 0);
    return Math.round(totalProgress / lessons.length);
  };

  const unitProgress = {
    unit1: calculateUnitProgress(["alpha", "finger", "greetings"]),
    unit2: calculateUnitProgress(["grammar", "nounAdj", "verbColors"]),
    unit3: 0, // Example, add lessons for Unit 3 if available
    unit4: 0, // Example, adjust for Final Exam if necessary
  };

  return (
    <div className="unitset-container">
      <h1 style={{ fontSize: "50px" }}>Units Dashboard</h1>
      <div className="unitset-list">
        {/* Unit 1 */}
        <div style={{ fontSize: "25px" }}>
          <button onClick={() => toggleUnit(1)} className="unit-button">
            Unit 1: Basics
            {quizStatus.alpha && quizStatus.finger && quizStatus.greetings && <span>üèÜ</span>}
          </button>
          <div className="progress-container">
            <CircularProgressbar
              value={unitProgress.unit1}
              text={`${unitProgress.unit1}%`}
              styles={{
                path: { stroke: unitProgress.unit1 === 100 ? "green" : "blue" },
                text: { fill: unitProgress.unit1 === 100 ? "green" : "blue" },
              }}
            />
          </div>
          {openUnit === 1 && (
            <div className="lesson-dropdown">
              <div className="lesson-container">
                <h3>Alphabet {quizStatus.alpha && <span>üèÜ</span>}</h3>
                <button onClick={() => handleNavigate("/units/lesson1")} className="lesson-button">
                  Go to Alphabet Lesson
                </button>
                <div className="progress-container">
                  <CircularProgressbar
                    value={progress.alpha}
                    text={`${progress.alpha}%`}
                    styles={{
                      path: { stroke: progress.alpha ? "green" : "red" },
                      text: { fill: progress.alpha ? "green" : "red" },
                    }}
                  />
                </div>
              </div>
              <div className="lesson-container">
                <h3>Finger Spelling {quizStatus.finger && <span>üèÜ</span>}</h3>
                <button onClick={() => handleNavigate("/units/lesson2")} className="lesson-button">
                  Go to Lesson
                </button>
                <div className="progress-container">
                  <CircularProgressbar
                    value={progress.finger}
                    text={`${progress.finger}%`}
                    styles={{
                      path: { stroke: progress.finger ? "green" : "red" },
                      text: { fill: progress.finger ? "green" : "red" },
                    }}
                  />
                </div>
              </div>
              <div className="lesson-container">
                <h3>Greetings {quizStatus.greetings && <span>üèÜ</span>}</h3>
                <button onClick={() => handleNavigate("/units/lesson3")} className="lesson-button">
                  Go to Lesson
                </button>
                <div className="progress-container">
                  <CircularProgressbar
                    value={progress.greetings}
                    text={`${progress.greetings}%`}
                    styles={{
                      path: { stroke: progress.greetings ? "green" : "red" },
                      text: { fill: progress.greetings ? "green" : "red" },
                    }}
                  />
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Add similar blocks for Unit 2, Unit 3, and Final Examination */}
      </div>
    </div>
  );
};

export default UnitSet;
